import React, { useEffect } from "react";
import "leaflet/dist/leaflet.css";

function LeafletMap() {
  useEffect(() => {
    const loadScript = (src) =>
      new Promise((resolve) => {
        const s = document.createElement("script");
        s.src = src;
        s.async = true;
        s.onload = resolve;
        document.body.appendChild(s);
      });

    Promise.all([
      loadScript("https://unpkg.com/leaflet/dist/leaflet.js"),
      loadScript("https://unpkg.com/papaparse@5.3.2/papaparse.min.js"),
    ]).then(() => {
      const L = window.L;
      const Papa = window.Papa;

      const map = L.map("map").setView([-6.8, 39.2], 6);
      L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
        attribution:
          '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a>',
      }).addTo(map);

      // You can now safely run your employerData logic here
      // For testing, add a dummy marker:
      L.marker([-6.8, 39.2]).addTo(map).bindPopup("Map is working!").openPopup();
    });
  }, []);

  return (
    <div>
      <div className="controls" style={{ margin: "10px", fontFamily: "sans-serif" }}>
        {/* Sliders and filters */}
        <label>
          Minimum Salary:
          <input type="range" id="salaryFilter" min="0" max="1500" step="50" defaultValue="0" />
          <span id="salaryValue">0</span>
        </label>
      </div>
      <div id="map" style={{ height: "600px", width: "100%" }}></div>
    </div>
  );
}

export default LeafletMap;
